import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/MusicMockup.png";
import projImg2 from "../assets/img/SignInMockup.png";
import projImg3 from "../assets/img/LandingPageMockup.png";
import projImg4 from "../assets/img/StreamingMockup.png";
import projImg5 from "../assets/img/NewsletterMockup.png";
import projImg6 from "../assets/img/Coming Soon.png"
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {

  const projects = [
    {
      title: "Music Streaming App",
      description: "Mockup",
      imgUrl: projImg1,
    },
    {
      title: "Sign In Page",
      description: "Mockup",
      imgUrl: projImg2,
    },
    {
      title: "E-Sports Landing Page",
      description: "Mockup",
      imgUrl: projImg3,
    },
    {
      title: "Movie Streaming App",
      description: "Mockup",
      imgUrl: projImg4,
    },
    {
      title: "Newsletter Pop-Up",
      description: "Mockup",
      imgUrl: projImg5,
    },
    {
      title: "Coming Soon",
      description: "More on Behance",
      imgUrl: projImg6,
    },
    
  ];

  const project = [
    {
      title: "Music Streaming App",
      description: "Mockup",
      imgUrl: projImg1,
    },
    {
      title: "Sign In Page",
      description: "Mockup",
      imgUrl: projImg2,
    },
    {
      title: "E-Sports Landing Page",
      description: "Mockup",
      imgUrl: projImg3,
    },
    {
      title: "Movie Streaming App",
      description: "Mockup",
      imgUrl: projImg4,
    },
    {
      title: "Newsletter Pop-Up",
      description: "Mockup",
      imgUrl: projImg5,
    },
    {
      title: "Coming Soon",
      description: "More on Behance",
      imgUrl: projImg6,
    },
  ];

  return (
    <section className="project" id="project">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn": ""}>
                <h2>Projects</h2>
                <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</p>
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                    <Nav.Item>
                      <Nav.Link eventKey="first">Mockups</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="second">Tab 2</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="third">Tab 3</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                    <Tab.Pane eventKey="first">
                      <Row>
                        {
                          projects.map((project, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...project}
                                />
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="second">
                      
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Cumque quam, quod neque provident velit, rem explicabo excepturi id illo molestiae blanditiis, eligendi dicta officiis asperiores delectus quasi inventore debitis quo.</p>
                    </Tab.Pane>
                    <Tab.Pane eventKey="third">
                      <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Cumque quam, quod neque provident velit, rem explicabo excepturi id illo molestiae blanditiis, eligendi dicta officiis asperiores delectus quasi inventore debitis quo.</p>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
